<?php require 'config.php'; $data = json_decode(file_get_contents('php://input'), true); if (!$data) { http_response_code(400); echo json_encode(['error'=>'no data']); exit; } $lat = $data['lat']; $lng = $data['lng']; $motorista_id = 1; $pdo->prepare('UPDATE motorista SET latitude=?, longitude=? WHERE id=?')->execute([$lat,$lng,$motorista_id]); echo json_encode(['status'=>'ok']); ?>