<?php require 'config.php'; if ($_SERVER['REQUEST_METHOD'] !== 'POST') { header('Location: ../cadastro_cliente.html'); exit; } $nome = $_POST['nome']; $email = $_POST['email']; $senha = password_hash($_POST['senha'], PASSWORD_DEFAULT); $endereco = $_POST['endereco']; $telefone = $_POST['telefone']; $pdo->prepare('INSERT INTO usuario (nome,email,senha,nivel_acesso) VALUES (?,?,?,"cliente")')->execute([$nome,$email,$senha]); $id = $pdo->lastInsertId(); $pdo->prepare('INSERT INTO cliente (id_usuario,endereco,telefone) VALUES (?,?,?)')->execute([$id,$endereco,$telefone]); echo 'Cliente cadastrado. <a href="../index.html">Voltar</a>'; ?>